import "./App.css";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import Modal from "./components/Modal";
import eventsData from "./Events.json";

const locales = {
  "en-US": require("date-fns/locale/en-US"),
};

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});


function App() {
  // Extract the events data array from the imported JSON data
  const { events } = eventsData;

  const [selected, setSelected] = useState(null);

  // captures the open / close state of the modal
  const [isOpen, setIsOpen] = useState(false);

  const handleSelected = (event) => {
    console.log("Selected Event:", event);
    setSelected(event);
    setIsOpen(true);
    // console.info('[handleSelected - event]', event);
    const eventDetails = event;
    // console.log(eventDetails);

  };


  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div className="App">
      <Calendar
        // selected={events.title}
        onSelectEvent={handleSelected}
        localizer={localizer}
        events={events}
        startAccessor="startdate"
        endAccessor="enddate"
        style={{ height: 750, margin: "50px" }}
        // onClick={console.log("test")}
      />

      // checks if the state of the modal is true for open and whether and event is selected before proceeding
      {isOpen && selected && (
        <Modal selectedEvent={selected} closeModal={closeModal} />
      )}
    </div>
  );
}

export default App;
