import "./App.css";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import React, { useState } from "react";
import DatePicker from "react-datepicker";

const locales = {
  "en-US": require("date-fns/locale/en-US"),
};

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

const events = [
  {
    title: "BIg MEeting",
    alldate: true,
    start: "2023-04-05",
    end: "2023-04-05",
  },
  {
    title: "Vacation",
    start: "2023-06-01",
    end: "2023-06-03",
  },
  {
    title: "Conference",
    start: new Date(2023, 6, 0),
    end: new Date(2023, 6, 0),
  },
];

function App() {
  const [selected, setSelected] = useState();

  // const [modalState, setModalState] = useState(false)
  const [isOpen, setIsOpen] = useState(false);

  // const handleSelectedEvent = (event) => {
  //    setSelectedEvent(event)
  //    setModalState(true)
  // }

  const handleSelected = (event) => {
    setSelected(event);
    setIsOpen(true);
    // console.info('[handleSelected - event]', event);
    const eventDetails = event;
    console.log(eventDetails);
    Modal(eventDetails);
  };

  const Modal = () => {
    return (
      // Here you define your modal, what you want it to contain.
      // Event title for example will be accessible via 'selectedEvent.title'

      <>
        <div>
          <h2>{selected.title}</h2>
          <p>
            {selected.start}
            {selected.end}
          </p>
          {/* <button> close </button> */}
        </div>
      </>
    );
  };

  return (
    <div className="App">
      <Calendar
        selected={events.title}
        onSelectEvent={handleSelected}
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 750, margin: "50px" }}
        onClick={console.log("test")}
      />

      {/* <Modal details={handleSelected}/> */}
    </div>
  );
}

export default App;
